            Button(action: {
                self.env.isRootNavitaionActive = false
            }) {
                Text("Tap this")
            }
        }
        .navigationBarTitle("Y")
    }
}

struct ZView: View {
    @EnvironmentObject var env: Env
    @State var text: String = "(Before tap)"

    var body: some View {
        VStack {
            Text(text)
            Button(action: {
                self.text = "(After tap)"
                self.env.isRootNavitaionActive = false
            }) {
                Text("Tap this")
            }
