    public var wrappedValue: T {
        get { return relay.value }
        set { relay.accept(newValue) }
    }

    public var projectedValue: BehaviorRelay<T> { relay }

    public init(wrappedValue: T) {
        self.relay = BehaviorRelay(value: wrappedValue)
    }
